const size_t XWUI_LOCAL_LANG_NAME_COUNT = sizeof(XWUI_LOCAL_LANG_NAMES) / sizeof(XWUI_LOCAL_LANG_NAMES[0]);

/////////////////////////////////////////////////////////////////////
// XWUILangNames - localized language names

// get localized language name from locale
bool XWUILangNames::getLangName(const char* locale, std::string& nameOut, std::wstring& localNameOut)
{
    XWASSERT(locale);
    if(locale == 0) return false;

    // reset output
    nameOut.clear();
    localNameOut.clear();

    size_t left, right, mid;
    int result;

    // search for locale
    left = 0;
    right = XWUI_LOCAL_LANG_NAME_COUNT;

    // binary search 
    while(left <= right && left < XWUI_LOCAL_LANG_NAME_COUNT)
    {
        // middle point 
        mid = left + (right - left) / 2;

        // compare locales
        result = ::_stricmp(locale, XWUI_LOCAL_LANG_NAMES[mid].locale);
        if(result == 0)
        {
            // copy output
            nameOut = XWUI_LOCAL_LANG_NAMES[mid].name;
            localNameOut = XWUI_LOCAL_LANG_NAMES[mid].lname;

            // locale found
            return true;
        }

        // next
        if(result < 0)
            right = mid - 1;
        else
            left = mid + 1;
    }

    // locale not found
    return false;
}

